import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-transactions-table',
  templateUrl: './transactions-table.component.html',
  styleUrls: ['./transactions-table.component.sass'],
})
export class TransactionsTableComponent {
  @Input() transactions: any[] = [];
  @Input() showFooter: boolean = false;
}
<table mat-table [dataSource]="transactions">
  <!-- Define the columns -->
  <ng-container matColumnDef="transactionId">
    <th mat-header-cell *matHeaderCellDef>Transaction ID</th>
    <td mat-cell *matCellDef="let transaction">
      {{ transaction.transactionId }}
    </td>
  </ng-container>

  <ng-container matColumnDef="amount">
    <th mat-header-cell *matHeaderCellDef>Amount</th>
    <td mat-cell *matCellDef="let transaction">{{ transaction.amount }}</td>
  </ng-container>

  <!-- More columns as needed -->

  <!-- Row content -->
  <tr mat-header-row *matHeaderRowDef="['transactionId', 'amount']"></tr>
  <tr
    mat-row
    *matRowDef="let transaction; columns: ['transactionId', 'amount']"
  ></tr>
</table>

<!-- Footer to display when there are no transactions -->
<div *ngIf="showFooter">
  <p class="table-footer">There are no transactions for this account.</p>
</div>
